# AStyle
# Run AStyle
.\AStyle.exe --mode=c --recursive --style=allman --indent=spaces=2 --indent-namespaces --indent-preproc-block --indent-col1-comments --pad-oper --pad-header --unpad-paren --align-pointer=type --attach-return-type --attach-return-type-decl --convert-tabs --close-templates --suffix=none --quiet *.cpp,*.h,*.ino
.\AStyle.exe --mode=java --recursive --style=allman --indent=spaces=2 --indent-namespaces --indent-preproc-block --indent-col1-comments --pad-oper --pad-header --unpad-paren --align-pointer=type --attach-return-type --attach-return-type-decl --convert-tabs --close-templates --suffix=none --quiet *.pde
